mapper.update:
UPDATE student
name = #{name}, no = #{no}, phone = #{phone}, gender = #{gender}, degree = #{degree}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, graduation_date = #{graduationDate}, violation_count = #{violationCount}, violation_score = #{violationScore}, clazz_id = #{clazzId}, update_time = NOW()
WHERE id = #{id}

UPDATE student
SET violation_count = violation_count + 1,
violation_score = violation_score + #{violationScore}  
WHERE id = #{id}
